export const getSkillLevelInfo = (level: number) => {
  if (level >= 90)
    return { label: "Expert", icon: Award, color: "text-yellow-600" };
  if (level >= 80)
    return { label: "Advanced", icon: TrendingUp, color: "text-green-600" };
  if (level >= 70)
    return { label: "Intermediate", icon: Zap, color: "text-blue-600" };
  return { label: "Beginner", icon: Star, color: "text-gray-600" };
};

export const getSkillsByCategory = (category: Skill["category"]) =>
  skills.filter((skill) => skill.category === category);

